703→<!-- Handle the dual view link -->
704→<script>
705→document.addEventListener("DOMContentLoaded", function () {
706→  const dualLink = document.getElementById("dualViewLink");
707→  if (!dualLink) return;
708→
709→  const isInDualView = window.location.pathname.startsWith("/dual");
710→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
711→  dualLink.addEventListener("click", function (e) {
712→    e.preventDefault();
713→
714→    if (isInDualView) {
715→      try {
716→        const leftIframe = document.getElementById("iframe-left");
717→        const src = leftIframe?.src || "/";
718→        let clean = src.replace(window.location.origin, "");
719→        clean = clean.replace(/[?&]embed=1/, "");
720→
721→        window.location.href = clean;
722→      } catch (err) {
723→        window.location.href = "/";
724→      }
725→    } else {
726→      const currentPath = window.location.pathname + window.location.search;
727→      const rightPanelDefault = "/";
728→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
729→      window.location.href = `/dual${hash}`;
730→    }
731→  });
732→});
733→</script>
