735→<!-- Redirect links and searches when in dual view mode -->
736→<script>
737→document.addEventListener("DOMContentLoaded", function () {
738→  if (!window.location.pathname.startsWith("/dual")) return;
739→
740→  function getTargetPanel() {
741→    const selected = document.querySelector('input[name="targetPanel"]:checked');
742→    return selected ? selected.value : "left";
743→  }
744→
745→  function loadInPanel(panel, path) {
746→    if (!path) return;
747→    const iframe = document.getElementById(`iframe-${panel}`);
748→    if (!iframe) return;
749→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
750→    iframe.src = url;
751→    const inputBox = document.querySelector(`#panel-${panel} input`);
752→    if (inputBox) inputBox.value = path;
753→    console.log(`Loaded "${url}" into panel "${panel}"`);
754→  }
755→  
756→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
757→    link.addEventListener("click", function (e) {
758→      e.preventDefault();
759→      const path = this.getAttribute("href");
760→      loadInPanel(getTargetPanel(), path);
761→    });
762→  });
763→
764→  const idForm = document.getElementById("idsearchForm");
765→  if (idForm) {
766→    idForm.addEventListener("submit", function (e) {
767→      e.preventDefault();
768→      const input = idForm.querySelector(".idsearchTerm").value.trim();
769→      if (!input) return;
770→
771→      let path = null;
772→
773→      if (/^\d+$/.test(input)) {
774→        path = "/go_to/" + encodeURIComponent(input);
775→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
776→        const parts = input.split(/\s+/).filter(Boolean);
777→        path = "/range/" + parts.map(encodeURIComponent).join("/");
778→      } else {
779→        alert("Invalid input format.");
780→        return;
781→      }
782→
783→      loadInPanel(getTargetPanel(), path);
784→    });
785→  }
786→
787→  const searchForm = document.getElementById("searchForm");
788→  if (searchForm) {
789→    searchForm.addEventListener("submit", function (e) {
790→      e.preventDefault();
791→      const input = searchForm.querySelector(".searchTerm").value.trim();
792→      if (!input) return;
793→
794→      const path = "/search/" + encodeURIComponent(input);
795→      loadInPanel(getTargetPanel(), path);
796→    });
797→  }
798→});
799→</script>
