637→<!-- Previewing forum posts -->
638→<script>
639→document.addEventListener("DOMContentLoaded", function () {
640→  function escapeHtml(str) {
641→    return str.replace(/[&<>"']/g, m => ({
642→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
643→    })[m]);
644→  }
645→  function normalizeHref(href){
646→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
647→      href = 'https://' + href;
648→    }
649→    return href;
650→  }
651→
652→  function renderPreview(textarea, preview, label) {
653→    let raw = textarea.value;
654→    const anchors = [];
655→    let idx = 0;
656→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
657→      (match, qHref, labelText) => {
658→        const href = qHref.slice(1, -1);
659→        const token = `__A_TAG_${idx++}__`;
660→        anchors.push({ token, href: normalizeHref(href), label: labelText });
661→        return token;
662→      });
663→    let safe = escapeHtml(raw);
664→    anchors.forEach(a => {
665→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
666→      safe = safe.replace(a.token, html);
667→    });
668→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
669→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
670→    );
671→    safe = safe.replace(/\n/g, "<br>");
672→    if (!safe.trim()) {
673→      preview.classList.add("hidden");
674→      label.classList.add("hidden");
675→      return;
676→    }
677→    preview.innerHTML = safe;
678→    preview.classList.remove("hidden");
679→    label.classList.remove("hidden");
680→    if (window.MathJax && MathJax.Hub) {
681→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
682→    }
683→  }
684→  document.querySelectorAll("form textarea").forEach(textarea => {
685→    const form = textarea.closest("form");
686→    const preview = form && form.querySelector(".post-preview");
687→    const label = form && form.querySelector(".preview-label");
688→    if (preview && label) {
689→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
690→      renderPreview(textarea, preview, label);
691→    }
692→  });
693→});
694→</script>
