805→    searchForm.addEventListener("submit", function (e) {
806→      e.preventDefault();
807→      const input = searchForm.querySelector(".searchTerm").value.trim();
808→      if (!input) return;
809→
810→      const path = "/search/" + encodeURIComponent(input);
811→      loadInPanel(getTargetPanel(), path);
812→    });
813→  }
814→});
