636→<!-- Previewing forum posts -->
637→<script>
638→document.addEventListener("DOMContentLoaded", function () {
639→  function escapeHtml(str) {
640→    return str.replace(/[&<>"']/g, m => ({
641→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
642→    })[m]);
643→  }
644→  function normalizeHref(href){
645→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
646→      href = 'https://' + href;
647→    }
648→    return href;
649→  }
650→
651→  function renderPreview(textarea, preview, label) {
652→    let raw = textarea.value;
653→    const anchors = [];
654→    let idx = 0;
655→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
656→      (match, qHref, labelText) => {
657→        const href = qHref.slice(1, -1);
658→        const token = `__A_TAG_${idx++}__`;
659→        anchors.push({ token, href: normalizeHref(href), label: labelText });
660→        return token;
661→      });
662→    let safe = escapeHtml(raw);
663→    anchors.forEach(a => {
664→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
665→      safe = safe.replace(a.token, html);
666→    });
667→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
668→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
669→    );
670→    safe = safe.replace(/\n/g, "<br>");
671→    if (!safe.trim()) {
672→      preview.classList.add("hidden");
673→      label.classList.add("hidden");
674→      return;
675→    }
676→    preview.innerHTML = safe;
677→    preview.classList.remove("hidden");
678→    label.classList.remove("hidden");
679→    if (window.MathJax && MathJax.Hub) {
680→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
681→    }
682→  }
683→  document.querySelectorAll("form textarea").forEach(textarea => {
684→    const form = textarea.closest("form");
685→    const preview = form && form.querySelector(".post-preview");
686→    const label = form && form.querySelector(".preview-label");
687→    if (preview && label) {
688→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
689→      renderPreview(textarea, preview, label);
690→    }
691→  });
692→});
693→</script>
