651→<!-- Previewing forum posts -->
652→<script>
653→document.addEventListener("DOMContentLoaded", function () {
654→  function escapeHtml(str) {
655→    return str.replace(/[&<>"']/g, m => ({
656→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
657→    })[m]);
658→  }
659→  function normalizeHref(href){
660→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
661→      href = 'https://' + href;
662→    }
663→    return href;
664→  }
665→
666→  function renderPreview(textarea, preview, label) {
667→    let raw = textarea.value;
668→    const anchors = [];
669→    let idx = 0;
670→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
671→      (match, qHref, labelText) => {
672→        const href = qHref.slice(1, -1);
673→        const token = `__A_TAG_${idx++}__`;
674→        anchors.push({ token, href: normalizeHref(href), label: labelText });
675→        return token;
676→      });
677→    let safe = escapeHtml(raw);
678→    anchors.forEach(a => {
679→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
680→      safe = safe.replace(a.token, html);
681→    });
682→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
683→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
684→    );
685→    safe = safe.replace(/\n/g, "<br>");
686→    if (!safe.trim()) {
687→      preview.classList.add("hidden");
688→      label.classList.add("hidden");
689→      return;
690→    }
691→    preview.innerHTML = safe;
692→    preview.classList.remove("hidden");
693→    label.classList.remove("hidden");
694→    if (window.MathJax && MathJax.Hub) {
695→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
696→    }
697→  }
698→  document.querySelectorAll("form textarea").forEach(textarea => {
699→    const form = textarea.closest("form");
700→    const preview = form && form.querySelector(".post-preview");
701→    const label = form && form.querySelector(".preview-label");
702→    if (preview && label) {
703→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
704→      renderPreview(textarea, preview, label);
705→    }
706→  });
707→});
708→</script>
