711→<!-- Handle the dual view link -->
712→<script>
713→document.addEventListener("DOMContentLoaded", function () {
714→  const dualLink = document.getElementById("dualViewLink");
715→  if (!dualLink) return;
716→
717→  const isInDualView = window.location.pathname.startsWith("/dual");
718→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
719→  dualLink.addEventListener("click", function (e) {
720→    e.preventDefault();
721→
722→    if (isInDualView) {
723→      try {
724→        const leftIframe = document.getElementById("iframe-left");
725→        const src = leftIframe?.src || "/";
726→        let clean = src.replace(window.location.origin, "");
727→        clean = clean.replace(/[?&]embed=1/, "");
728→
729→        window.location.href = clean;
730→      } catch (err) {
731→        window.location.href = "/";
732→      }
733→    } else {
734→      const currentPath = window.location.pathname + window.location.search;
735→      const rightPanelDefault = "/";
736→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
737→      window.location.href = `/dual${hash}`;
738→    }
739→  });
740→});
741→</script>
