743→<!-- Redirect links and searches when in dual view mode -->
744→<script>
745→document.addEventListener("DOMContentLoaded", function () {
746→  if (!window.location.pathname.startsWith("/dual")) return;
747→
748→  function getTargetPanel() {
749→    const selected = document.querySelector('input[name="targetPanel"]:checked');
750→    return selected ? selected.value : "left";
751→  }
752→
753→  function loadInPanel(panel, path) {
754→    if (!path) return;
755→    const iframe = document.getElementById(`iframe-${panel}`);
756→    if (!iframe) return;
757→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
758→    iframe.src = url;
759→    const inputBox = document.querySelector(`#panel-${panel} input`);
760→    if (inputBox) inputBox.value = path;
761→    console.log(`Loaded "${url}" into panel "${panel}"`);
762→  }
763→  
764→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
765→    link.addEventListener("click", function (e) {
766→      e.preventDefault();
767→      const path = this.getAttribute("href");
768→      loadInPanel(getTargetPanel(), path);
769→    });
770→  });
771→
772→  const idForm = document.getElementById("idsearchForm");
773→  if (idForm) {
774→    idForm.addEventListener("submit", function (e) {
775→      e.preventDefault();
776→      const input = idForm.querySelector(".idsearchTerm").value.trim();
777→      if (!input) return;
778→
779→      let path = null;
780→
781→      if (/^\d+$/.test(input)) {
782→        path = "/go_to/" + encodeURIComponent(input);
783→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
784→        const parts = input.split(/\s+/).filter(Boolean);
785→        path = "/range/" + parts.map(encodeURIComponent).join("/");
786→      } else {
787→        alert("Invalid input format.");
788→        return;
789→      }
790→
791→      loadInPanel(getTargetPanel(), path);
792→    });
793→  }
794→
795→  const searchForm = document.getElementById("searchForm");
796→  if (searchForm) {
797→    searchForm.addEventListener("submit", function (e) {
798→      e.preventDefault();
799→      const input = searchForm.querySelector(".searchTerm").value.trim();
800→      if (!input) return;
801→
802→      const path = "/search/" + encodeURIComponent(input);
803→      loadInPanel(getTargetPanel(), path);
804→    });
805→  }
806→});
807→</script>
