644→    }
645→    return href;
646→  }
647→
648→  function renderPreview(textarea, preview, label) {
649→    let raw = textarea.value;
650→    const anchors = [];
651→    let idx = 0;
652→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
653→      (match, qHref, labelText) => {
654→        const href = qHref.slice(1, -1);
655→        const token = `__A_TAG_${idx++}__`;
656→        anchors.push({ token, href: normalizeHref(href), label: labelText });
657→        return token;
658→      });
659→    let safe = escapeHtml(raw);
660→    anchors.forEach(a => {
661→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
662→      safe = safe.replace(a.token, html);
663→    });
664→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
665→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
666→    );
667→    safe = safe.replace(/\n/g, "<br>");
668→    if (!safe.trim()) {
669→      preview.classList.add("hidden");
670→      label.classList.add("hidden");
671→      return;
672→    }
673→    preview.innerHTML = safe;
674→    preview.classList.remove("hidden");
675→    label.classList.remove("hidden");
676→    if (window.MathJax && MathJax.Hub) {
677→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
678→    }
679→  }
680→  document.querySelectorAll("form textarea").forEach(textarea => {
681→    const form = textarea.closest("form");
682→    const preview = form && form.querySelector(".post-preview");
683→    const label = form && form.querySelector(".preview-label");
684→    if (preview && label) {
685→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
686→      renderPreview(textarea, preview, label);
687→    }
688→  });
689→});
690→</script>
