   618→<!-- Previewing forum posts -->
   619→<script>
   620→document.addEventListener("DOMContentLoaded", function () {
   621→  function escapeHtml(str) {
   622→    return str.replace(/[&<>"']/g, m => ({
   623→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
   624→    })[m]);
   625→  }
   626→  function normalizeHref(href){
   627→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
   628→      href = 'https://' + href;
   629→    }
   630→    return href;
   631→  }
   632→
   633→  function renderPreview(textarea, preview, label) {
   634→    let raw = textarea.value;
   635→    const anchors = [];
   636→    let idx = 0;
   637→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
   638→      (match, qHref, labelText) => {
   639→        const href = qHref.slice(1, -1);
   640→        const token = `__A_TAG_${idx++}__`;
   641→        anchors.push({ token, href: normalizeHref(href), label: labelText });
   642→        return token;
   643→      });
   644→    let safe = escapeHtml(raw);
   645→    anchors.forEach(a => {
   646→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
   647→      safe = safe.replace(a.token, html);
   648→    });
   649→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
   650→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
   651→    );
   652→    safe = safe.replace(/\n/g, "<br>");
   653→    if (!safe.trim()) {
   654→      preview.classList.add("hidden");
   655→      label.classList.add("hidden");
   656→      return;
   657→    }
   658→    preview.innerHTML = safe;
   659→    preview.classList.remove("hidden");
   660→    label.classList.remove("hidden");
   661→    if (window.MathJax && MathJax.Hub) {
   662→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
   663→    }
   664→  }
   665→  document.querySelectorAll("form textarea").forEach(textarea => {
   666→    const form = textarea.closest("form");
   667→    const preview = form && form.querySelector(".post-preview");
   668→    const label = form && form.querySelector(".preview-label");
   669→    if (preview && label) {
   670→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
   671→      renderPreview(textarea, preview, label);
   672→    }
   673→  });
   674→});
   675→</script>
