629→<!-- Previewing forum posts -->
630→<script>
631→document.addEventListener("DOMContentLoaded", function () {
632→  function escapeHtml(str) {
633→    return str.replace(/[&<>"']/g, m => ({
634→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
635→    })[m]);
636→  }
637→  function normalizeHref(href){
638→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
639→      href = 'https://' + href;
640→    }
641→    return href;
642→  }
643→
644→  function renderPreview(textarea, preview, label) {
645→    let raw = textarea.value;
646→    const anchors = [];
647→    let idx = 0;
648→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
649→      (match, qHref, labelText) => {
650→        const href = qHref.slice(1, -1);
651→        const token = `__A_TAG_${idx++}__`;
652→        anchors.push({ token, href: normalizeHref(href), label: labelText });
653→        return token;
654→      });
655→    let safe = escapeHtml(raw);
656→    anchors.forEach(a => {
657→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
658→      safe = safe.replace(a.token, html);
659→    });
660→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
661→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
662→    );
663→    safe = safe.replace(/\n/g, "<br>");
664→    if (!safe.trim()) {
665→      preview.classList.add("hidden");
666→      label.classList.add("hidden");
667→      return;
668→    }
669→    preview.innerHTML = safe;
670→    preview.classList.remove("hidden");
671→    label.classList.remove("hidden");
672→    if (window.MathJax && MathJax.Hub) {
673→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
674→    }
675→  }
676→  document.querySelectorAll("form textarea").forEach(textarea => {
677→    const form = textarea.closest("form");
678→    const preview = form && form.querySelector(".post-preview");
679→    const label = form && form.querySelector(".preview-label");
680→    if (preview && label) {
681→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
682→      renderPreview(textarea, preview, label);
683→    }
684→  });
685→});
686→</script>
