617→<script>
618→document.addEventListener("DOMContentLoaded", function () {
619→  function escapeHtml(str) {
620→    return str.replace(/[&<>"']/g, m => ({
621→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
622→    })[m]);
623→  }
624→  function normalizeHref(href){
625→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
626→      href = 'https://' + href;
627→    }
628→    return href;
629→  }
630→
631→  function renderPreview(textarea, preview, label) {
632→    let raw = textarea.value;
633→    const anchors = [];
634→    let idx = 0;
635→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
636→      (match, qHref, labelText) => {
637→        const href = qHref.slice(1, -1);
638→        const token = `__A_TAG_${idx++}__`;
639→        anchors.push({ token, href: normalizeHref(href), label: labelText });
640→        return token;
641→      });
642→    let safe = escapeHtml(raw);
643→    anchors.forEach(a => {
644→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
645→      safe = safe.replace(a.token, html);
646→    });
647→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
648→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
649→    );
650→    safe = safe.replace(/\n/g, "<br>");
651→    if (!safe.trim()) {
652→      preview.classList.add("hidden");
