643→document.addEventListener("DOMContentLoaded", function () {
644→  function escapeHtml(str) {
645→    return str.replace(/[&<>"']/g, m => ({
646→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
647→    })[m]);
648→  }
649→  function normalizeHref(href){
650→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
651→      href = 'https://' + href;
652→    }
653→    return href;
654→  }
655→
656→  function renderPreview(textarea, preview, label) {
657→    let raw = textarea.value;
658→    const anchors = [];
659→    let idx = 0;
660→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
661→      (match, qHref, labelText) => {
662→        const href = qHref.slice(1, -1);
663→        const token = `__A_TAG_${idx++}__`;
664→        anchors.push({ token, href: normalizeHref(href), label: labelText });
665→        return token;
666→      });
667→    let safe = escapeHtml(raw);
668→    anchors.forEach(a => {
669→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
670→      safe = safe.replace(a.token, html);
671→    });
672→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
673→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
674→    );
675→    safe = safe.replace(/\n/g, "<br>");
676→    if (!safe.trim()) {
677→      preview.classList.add("hidden");
678→      label.classList.add("hidden");
679→      return;
680→    }
681→    preview.innerHTML = safe;
682→    preview.classList.remove("hidden");
683→    label.classList.remove("hidden");
684→    if (window.MathJax && MathJax.Hub) {
685→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
686→    }
687→  }
688→  document.querySelectorAll("form textarea").forEach(textarea => {
689→    const form = textarea.closest("form");
690→    const preview = form && form.querySelector(".post-preview");
691→    const label = form && form.querySelector(".preview-label");
692→    if (preview && label) {
693→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
694→      renderPreview(textarea, preview, label);
695→    }
696→  });
697→});
698→</script>
