631→  function renderPreview(textarea, preview, label) {
632→    let raw = textarea.value;
633→    const anchors = [];
634→    let idx = 0;
635→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
636→      (match, qHref, labelText) => {
637→        const href = qHref.slice(1, -1);
638→        const token = `__A_TAG_${idx++}__`;
639→        anchors.push({ token, href: normalizeHref(href), label: labelText });
640→        return token;
641→      });
642→    let safe = escapeHtml(raw);
643→    anchors.forEach(a => {
644→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
645→      safe = safe.replace(a.token, html);
646→    });
647→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
648→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
649→    );
650→    safe = safe.replace(/\n/g, "<br>");
651→    if (!safe.trim()) {
652→      preview.classList.add("hidden");
653→      label.classList.add("hidden");
654→      return;
655→    }
656→    preview.innerHTML = safe;
657→    preview.classList.remove("hidden");
658→    label.classList.remove("hidden");
659→    if (window.MathJax && MathJax.Hub) {
660→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
661→    }
662→  }
663→  document.querySelectorAll("form textarea").forEach(textarea => {
664→    const form = textarea.closest("form");
665→    const preview = form && form.querySelector(".post-preview");
666→    const label = form && form.querySelector(".preview-label");
667→    if (preview && label) {
668→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
669→      renderPreview(textarea, preview, label);
670→    }
671→  });
672→});
673→</script>
