698→<!-- Handle the dual view link -->
699→<script>
700→document.addEventListener("DOMContentLoaded", function () {
701→  const dualLink = document.getElementById("dualViewLink");
702→  if (!dualLink) return;
703→
704→  const isInDualView = window.location.pathname.startsWith("/dual");
705→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
706→  dualLink.addEventListener("click", function (e) {
707→    e.preventDefault();
708→
709→    if (isInDualView) {
710→      try {
711→        const leftIframe = document.getElementById("iframe-left");
712→        const src = leftIframe?.src || "/";
713→        let clean = src.replace(window.location.origin, "");
714→        clean = clean.replace(/[?&]embed=1/, "");
715→
716→        window.location.href = clean;
717→      } catch (err) {
718→        window.location.href = "/";
719→      }
720→    } else {
721→      const currentPath = window.location.pathname + window.location.search;
722→      const rightPanelDefault = "/";
723→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
724→      window.location.href = `/dual${hash}`;
725→    }
726→  });
727→});
728→</script>
