730→<!-- Redirect links and searches when in dual view mode -->
731→<script>
732→document.addEventListener("DOMContentLoaded", function () {
733→  if (!window.location.pathname.startsWith("/dual")) return;
734→
735→  function getTargetPanel() {
736→    const selected = document.querySelector('input[name="targetPanel"]:checked');
737→    return selected ? selected.value : "left";
738→  }
739→
740→  function loadInPanel(panel, path) {
741→    if (!path) return;
742→    const iframe = document.getElementById(`iframe-${panel}`);
743→    if (!iframe) return;
744→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
745→    iframe.src = url;
746→    const inputBox = document.querySelector(`#panel-${panel} input`);
747→    if (inputBox) inputBox.value = path;
748→    console.log(`Loaded "${url}" into panel "${panel}"`);
749→  }
750→  
751→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
752→    link.addEventListener("click", function (e) {
753→      e.preventDefault();
754→      const path = this.getAttribute("href");
755→      loadInPanel(getTargetPanel(), path);
756→    });
757→  });
758→
759→  const idForm = document.getElementById("idsearchForm");
760→  if (idForm) {
761→    idForm.addEventListener("submit", function (e) {
762→      e.preventDefault();
763→      const input = idForm.querySelector(".idsearchTerm").value.trim();
764→      if (!input) return;
765→
766→      let path = null;
767→
768→      if (/^\d+$/.test(input)) {
769→        path = "/go_to/" + encodeURIComponent(input);
770→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
771→        const parts = input.split(/\s+/).filter(Boolean);
772→        path = "/range/" + parts.map(encodeURIComponent).join("/");
773→      } else {
774→        alert("Invalid input format.");
775→        return;
776→      }
777→
778→      loadInPanel(getTargetPanel(), path);
779→    });
780→  }
781→
782→  const searchForm = document.getElementById("searchForm");
783→  if (searchForm) {
784→    searchForm.addEventListener("submit", function (e) {
785→      e.preventDefault();
786→      const input = searchForm.querySelector(".searchTerm").value.trim();
787→      if (!input) return;
788→
789→      const path = "/search/" + encodeURIComponent(input);
790→      loadInPanel(getTargetPanel(), path);
791→    });
792→  }
793→});
794→</script>
