642→<!-- Previewing forum posts -->
643→<script>
644→document.addEventListener("DOMContentLoaded", function () {
645→  function escapeHtml(str) {
646→    return str.replace(/[&<>"']/g, m => ({
647→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
648→    })[m]);
649→  }
650→  function normalizeHref(href){
651→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
652→      href = 'https://' + href;
653→    }
654→    return href;
655→  }
656→
657→  function renderPreview(textarea, preview, label) {
658→    let raw = textarea.value;
659→    const anchors = [];
660→    let idx = 0;
661→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
662→      (match, qHref, labelText) => {
663→        const href = qHref.slice(1, -1);
664→        const token = `__A_TAG_${idx++}__`;
665→        anchors.push({ token, href: normalizeHref(href), label: labelText });
666→        return token;
667→      });
668→    let safe = escapeHtml(raw);
669→    anchors.forEach(a => {
670→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
671→      safe = safe.replace(a.token, html);
672→    });
673→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
674→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
675→    );
676→    safe = safe.replace(/\n/g, "<br>");
677→    if (!safe.trim()) {
678→      preview.classList.add("hidden");
679→      label.classList.add("hidden");
680→      return;
681→    }
682→    preview.innerHTML = safe;
683→    preview.classList.remove("hidden");
684→    label.classList.remove("hidden");
685→    if (window.MathJax && MathJax.Hub) {
686→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
687→    }
688→  }
689→  document.querySelectorAll("form textarea").forEach(textarea => {
690→    const form = textarea.closest("form");
691→    const preview = form && form.querySelector(".post-preview");
692→    const label = form && form.querySelector(".preview-label");
693→    if (preview && label) {
694→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
695→      renderPreview(textarea, preview, label);
696→    }
697→  });
698→});
699→</script>
