777→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
778→    link.addEventListener("click", function (e) {
779→      e.preventDefault();
780→      const path = this.getAttribute("href");
781→      loadInPanel(getTargetPanel(), path);
782→    });
