799→      } else {
800→        alert("Invalid input format.");
801→        return;
802→      }
803→
804→      loadInPanel(getTargetPanel(), path);
805→    });
806→  }
807→
808→  const searchForm = document.getElementById("searchForm");
809→  if (searchForm) {
810→    searchForm.addEventListener("submit", function (e) {
811→      e.preventDefault();
812→      const input = searchForm.querySelector(".searchTerm").value.trim();
813→      if (!input) return;
814→
815→      const path = "/search/" + encodeURIComponent(input);
816→      loadInPanel(getTargetPanel(), path);
817→    });
818→  }
819→});
