639→  function renderPreview(textarea, preview, label) {
640→    let raw = textarea.value;
641→    const anchors = [];
642→    let idx = 0;
643→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
644→      (match, qHref, labelText) => {
645→        const href = qHref.slice(1, -1);
646→        const token = `__A_TAG_${idx++}__`;
647→        anchors.push({ token, href: normalizeHref(href), label: labelText });
