628→<!-- Previewing forum posts -->
629→<script>
630→document.addEventListener("DOMContentLoaded", function () {
631→  function escapeHtml(str) {
632→    return str.replace(/[&<>"']/g, m => ({
633→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
634→    })[m]);
635→  }
636→  function normalizeHref(href){
637→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
638→      href = 'https://' + href;
639→    }
640→    return href;
641→  }
642→
643→  function renderPreview(textarea, preview, label) {
644→    let raw = textarea.value;
645→    const anchors = [];
646→    let idx = 0;
647→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
648→      (match, qHref, labelText) => {
649→        const href = qHref.slice(1, -1);
650→        const token = `__A_TAG_${idx++}__`;
651→        anchors.push({ token, href: normalizeHref(href), label: labelText });
652→        return token;
653→      });
654→    let safe = escapeHtml(raw);
655→    anchors.forEach(a => {
656→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
657→      safe = safe.replace(a.token, html);
658→    });
659→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
660→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
661→    );
662→    safe = safe.replace(/\n/g, "<br>");
663→    if (!safe.trim()) {
664→      preview.classList.add("hidden");
665→      label.classList.add("hidden");
666→      return;
667→    }
668→    preview.innerHTML = safe;
669→    preview.classList.remove("hidden");
670→    label.classList.remove("hidden");
671→    if (window.MathJax && MathJax.Hub) {
672→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
673→    }
674→  }
675→  document.querySelectorAll("form textarea").forEach(textarea => {
676→    const form = textarea.closest("form");
677→    const preview = form && form.querySelector(".post-preview");
678→    const label = form && form.querySelector(".preview-label");
679→    if (preview && label) {
680→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
681→      renderPreview(textarea, preview, label);
682→    }
683→  });
684→});
685→</script>
