794→    searchForm.addEventListener("submit", function (e) {
795→      e.preventDefault();
796→      const input = searchForm.querySelector(".searchTerm").value.trim();
797→      if (!input) return;
798→
799→      const path = "/search/" + encodeURIComponent(input);
800→      loadInPanel(getTargetPanel(), path);
801→    });
802→  }
803→});
