638→  }
639→
640→  function renderPreview(textarea, preview, label) {
641→    let raw = textarea.value;
642→    const anchors = [];
643→    let idx = 0;
644→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
645→      (match, qHref, labelText) => {
646→        const href = qHref.slice(1, -1);
647→        const token = `__A_TAG_${idx++}__`;
648→        anchors.push({ token, href: normalizeHref(href), label: labelText });
649→        return token;
650→      });
651→    let safe = escapeHtml(raw);
652→    anchors.forEach(a => {
653→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
654→      safe = safe.replace(a.token, html);
655→    });
656→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
657→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
658→    );
659→    safe = safe.replace(/\n/g, "<br>");
660→    if (!safe.trim()) {
661→      preview.classList.add("hidden");
662→      label.classList.add("hidden");
663→      return;
664→    }
665→    preview.innerHTML = safe;
666→    preview.classList.remove("hidden");
667→    label.classList.remove("hidden");
668→    if (window.MathJax && MathJax.Hub) {
669→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
670→    }
671→  }
672→  document.querySelectorAll("form textarea").forEach(textarea => {
673→    const form = textarea.closest("form");
674→    const preview = form && form.querySelector(".post-preview");
675→    const label = form && form.querySelector(".preview-label");
676→    if (preview && label) {
677→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
678→      renderPreview(textarea, preview, label);
679→    }
680→  });
681→});
682→</script>
