    26→  if (quoteBtn) {
    27→    const id = quoteBtn.getAttribute('data-target');
    28→    const form = document.getElementById(id);
    29→    if (!form) return;
    30→    const ta = form.querySelector('textarea');
    31→    const raw = quoteBtn.getAttribute('data-raw') || '';
