645→<!-- Previewing forum posts -->
646→<script>
647→document.addEventListener("DOMContentLoaded", function () {
648→  function escapeHtml(str) {
649→    return str.replace(/[&<>"']/g, m => ({
650→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
651→    })[m]);
652→  }
653→  function normalizeHref(href){
654→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
655→      href = 'https://' + href;
656→    }
657→    return href;
658→  }
659→
660→  function renderPreview(textarea, preview, label) {
661→    let raw = textarea.value;
662→    const anchors = [];
663→    let idx = 0;
664→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
665→      (match, qHref, labelText) => {
666→        const href = qHref.slice(1, -1);
667→        const token = `__A_TAG_${idx++}__`;
668→        anchors.push({ token, href: normalizeHref(href), label: labelText });
669→        return token;
670→      });
671→    let safe = escapeHtml(raw);
672→    anchors.forEach(a => {
673→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
674→      safe = safe.replace(a.token, html);
675→    });
676→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
677→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
678→    );
679→    safe = safe.replace(/\n/g, "<br>");
680→    if (!safe.trim()) {
681→      preview.classList.add("hidden");
682→      label.classList.add("hidden");
683→      return;
684→    }
685→    preview.innerHTML = safe;
686→    preview.classList.remove("hidden");
687→    label.classList.remove("hidden");
688→    if (window.MathJax && MathJax.Hub) {
689→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
690→    }
691→  }
692→  document.querySelectorAll("form textarea").forEach(textarea => {
693→    const form = textarea.closest("form");
694→    const preview = form && form.querySelector(".post-preview");
695→    const label = form && form.querySelector(".preview-label");
696→    if (preview && label) {
697→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
698→      renderPreview(textarea, preview, label);
699→    }
700→  });
701→});
702→</script>
