649→<!-- Previewing forum posts -->
650→<script>
651→document.addEventListener("DOMContentLoaded", function () {
652→  function escapeHtml(str) {
653→    return str.replace(/[&<>"']/g, m => ({
654→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
655→    })[m]);
656→  }
657→  function normalizeHref(href){
658→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
659→      href = 'https://' + href;
660→    }
661→    return href;
662→  }
663→
664→  function renderPreview(textarea, preview, label) {
665→    let raw = textarea.value;
666→    const anchors = [];
667→    let idx = 0;
668→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
669→      (match, qHref, labelText) => {
670→        const href = qHref.slice(1, -1);
671→        const token = `__A_TAG_${idx++}__`;
672→        anchors.push({ token, href: normalizeHref(href), label: labelText });
673→        return token;
674→      });
675→    let safe = escapeHtml(raw);
676→    anchors.forEach(a => {
677→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
678→      safe = safe.replace(a.token, html);
679→    });
680→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
681→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
682→    );
683→    safe = safe.replace(/\n/g, "<br>");
684→    if (!safe.trim()) {
685→      preview.classList.add("hidden");
686→      label.classList.add("hidden");
687→      return;
688→    }
689→    preview.innerHTML = safe;
690→    preview.classList.remove("hidden");
691→    label.classList.remove("hidden");
692→    if (window.MathJax && MathJax.Hub) {
693→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
694→    }
695→  }
696→  document.querySelectorAll("form textarea").forEach(textarea => {
697→    const form = textarea.closest("form");
698→    const preview = form && form.querySelector(".post-preview");
699→    const label = form && form.querySelector(".preview-label");
700→    if (preview && label) {
701→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
702→      renderPreview(textarea, preview, label);
703→    }
704→  });
705→});
706→</script>
