625→  function escapeHtml(str) {
626→    return str.replace(/[&<>"']/g, m => ({
627→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
628→    })[m]);
629→  }
630→  function normalizeHref(href){
631→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
632→      href = 'https://' + href;
633→    }
634→    return href;
635→  }
636→
637→  function renderPreview(textarea, preview, label) {
638→    let raw = textarea.value;
639→    const anchors = [];
640→    let idx = 0;
641→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
642→      (match, qHref, labelText) => {
643→        const href = qHref.slice(1, -1);
644→        const token = `__A_TAG_${idx++}__`;
645→        anchors.push({ token, href: normalizeHref(href), label: labelText });
646→        return token;
647→      });
648→    let safe = escapeHtml(raw);
649→    anchors.forEach(a => {
650→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
651→      safe = safe.replace(a.token, html);
652→    });
653→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
654→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
655→    );
656→    safe = safe.replace(/\n/g, "<br>");
657→    if (!safe.trim()) {
658→      preview.classList.add("hidden");
659→      label.classList.add("hidden");
660→      return;
661→    }
662→    preview.innerHTML = safe;
663→    preview.classList.remove("hidden");
664→    label.classList.remove("hidden");
665→    if (window.MathJax && MathJax.Hub) {
666→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
667→    }
668→  }
669→  document.querySelectorAll("form textarea").forEach(textarea => {
670→    const form = textarea.closest("form");
671→    const preview = form && form.querySelector(".post-preview");
672→    const label = form && form.querySelector(".preview-label");
673→    if (preview && label) {
674→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
675→      renderPreview(textarea, preview, label);
676→    }
677→  });
678→});
679→</script>
