681→<!-- To handle the search bar -->
682→<script>
683→document.addEventListener('DOMContentLoaded', function () {
684→    function setFilterLink(linkId, newFilter) {
685→        const link = document.getElementById(linkId);
686→        if (!link) return;
687→        const pathParts = window.location.pathname.split("/");
688→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
689→        if (pathParts.length >= 3) {
690→            const section = pathParts[1];
691→            const entry = pathParts[2];
692→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
693→            const baseUrl = "/" + section + "/" + entry;
694→            link.href = baseUrl + "/" + newFilter;
695→        }
696→    }
697→    setFilterLink("solvedLink", "solved");
698→    setFilterLink("openLink", "open");
699→    setFilterLink("yesLink", "yes");
700→    setFilterLink("noLink", "no");
701→});
