704→<!-- Handle the dual view link -->
705→<script>
706→document.addEventListener("DOMContentLoaded", function () {
707→  const dualLink = document.getElementById("dualViewLink");
708→  if (!dualLink) return;
709→
710→  const isInDualView = window.location.pathname.startsWith("/dual");
711→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
712→  dualLink.addEventListener("click", function (e) {
713→    e.preventDefault();
714→
715→    if (isInDualView) {
716→      try {
717→        const leftIframe = document.getElementById("iframe-left");
718→        const src = leftIframe?.src || "/";
719→        let clean = src.replace(window.location.origin, "");
720→        clean = clean.replace(/[?&]embed=1/, "");
721→
722→        window.location.href = clean;
723→      } catch (err) {
724→        window.location.href = "/";
725→      }
726→    } else {
727→      const currentPath = window.location.pathname + window.location.search;
728→      const rightPanelDefault = "/";
729→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
730→      window.location.href = `/dual${hash}`;
731→    }
732→  });
733→});
734→</script>
