736→<!-- Redirect links and searches when in dual view mode -->
737→<script>
738→document.addEventListener("DOMContentLoaded", function () {
739→  if (!window.location.pathname.startsWith("/dual")) return;
740→
741→  function getTargetPanel() {
742→    const selected = document.querySelector('input[name="targetPanel"]:checked');
743→    return selected ? selected.value : "left";
744→  }
745→
746→  function loadInPanel(panel, path) {
747→    if (!path) return;
748→    const iframe = document.getElementById(`iframe-${panel}`);
749→    if (!iframe) return;
750→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
751→    iframe.src = url;
752→    const inputBox = document.querySelector(`#panel-${panel} input`);
753→    if (inputBox) inputBox.value = path;
754→    console.log(`Loaded "${url}" into panel "${panel}"`);
755→  }
756→  
757→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
758→    link.addEventListener("click", function (e) {
759→      e.preventDefault();
760→      const path = this.getAttribute("href");
761→      loadInPanel(getTargetPanel(), path);
762→    });
763→  });
764→
765→  const idForm = document.getElementById("idsearchForm");
766→  if (idForm) {
767→    idForm.addEventListener("submit", function (e) {
768→      e.preventDefault();
769→      const input = idForm.querySelector(".idsearchTerm").value.trim();
770→      if (!input) return;
771→
772→      let path = null;
773→
774→      if (/^\d+$/.test(input)) {
775→        path = "/go_to/" + encodeURIComponent(input);
776→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
777→        const parts = input.split(/\s+/).filter(Boolean);
778→        path = "/range/" + parts.map(encodeURIComponent).join("/");
779→      } else {
780→        alert("Invalid input format.");
781→        return;
782→      }
783→
784→      loadInPanel(getTargetPanel(), path);
785→    });
786→  }
787→
788→  const searchForm = document.getElementById("searchForm");
789→  if (searchForm) {
790→    searchForm.addEventListener("submit", function (e) {
791→      e.preventDefault();
792→      const input = searchForm.querySelector(".searchTerm").value.trim();
793→      if (!input) return;
794→
795→      const path = "/search/" + encodeURIComponent(input);
796→      loadInPanel(getTargetPanel(), path);
797→    });
798→  }
799→});
800→</script>
