   617→  function escapeHtml(str) {
   618→    return str.replace(/[&<>"']/g, m => ({
   619→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
   620→    })[m]);
   621→  }
   622→  function normalizeHref(href){
   623→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
   624→      href = 'https://' + href;
   625→    }
   626→    return href;
   627→  }
   628→
   629→  function renderPreview(textarea, preview, label) {
   630→    let raw = textarea.value;
   631→    const anchors = [];
   632→    let idx = 0;
   633→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
   634→      (match, qHref, labelText) => {
   635→        const href = qHref.slice(1, -1);
   636→        const token = `__A_TAG_${idx++}__`;
   637→        anchors.push({ token, href: normalizeHref(href), label: labelText });
   638→        return token;
   639→      });
   640→    let safe = escapeHtml(raw);
   641→    anchors.forEach(a => {
   642→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
   643→      safe = safe.replace(a.token, html);
   644→    });
   645→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
   646→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
   647→    );
   648→    safe = safe.replace(/\n/g, "<br>");
   649→    if (!safe.trim()) {
   650→      preview.classList.add("hidden");
   651→      label.classList.add("hidden");
   652→      return;
   653→    }
   654→    preview.innerHTML = safe;
   655→    preview.classList.remove("hidden");
   656→    label.classList.remove("hidden");
   657→    if (window.MathJax && MathJax.Hub) {
   658→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
   659→    }
   660→  }
   661→  document.querySelectorAll("form textarea").forEach(textarea => {
   662→    const form = textarea.closest("form");
   663→    const preview = form && form.querySelector(".post-preview");
   664→    const label = form && form.querySelector(".preview-label");
   665→    if (preview && label) {
   666→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
   667→      renderPreview(textarea, preview, label);
   668→    }
   669→  });
   670→});
   671→</script>
