   620→<!-- Previewing forum posts -->
   621→<script>
   622→document.addEventListener("DOMContentLoaded", function () {
   623→  function escapeHtml(str) {
   624→    return str.replace(/[&<>"']/g, m => ({
   625→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
   626→    })[m]);
   627→  }
   628→  function normalizeHref(href){
   629→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
   630→      href = 'https://' + href;
   631→    }
   632→    return href;
   633→  }
   634→
   635→  function renderPreview(textarea, preview, label) {
   636→    let raw = textarea.value;
   637→    const anchors = [];
   638→    let idx = 0;
   639→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
   640→      (match, qHref, labelText) => {
   641→        const href = qHref.slice(1, -1);
   642→        const token = `__A_TAG_${idx++}__`;
   643→        anchors.push({ token, href: normalizeHref(href), label: labelText });
   644→        return token;
   645→      });
   646→    let safe = escapeHtml(raw);
   647→    anchors.forEach(a => {
   648→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
   649→      safe = safe.replace(a.token, html);
   650→    });
   651→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
   652→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
   653→    );
   654→    safe = safe.replace(/\n/g, "<br>");
   655→    if (!safe.trim()) {
   656→      preview.classList.add("hidden");
   657→      label.classList.add("hidden");
   658→      return;
   659→    }
   660→    preview.innerHTML = safe;
   661→    preview.classList.remove("hidden");
   662→    label.classList.remove("hidden");
   663→    if (window.MathJax && MathJax.Hub) {
   664→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
   665→    }
   666→  }
   667→  document.querySelectorAll("form textarea").forEach(textarea => {
   668→    const form = textarea.closest("form");
   669→    const preview = form && form.querySelector(".post-preview");
   670→    const label = form && form.querySelector(".preview-label");
   671→    if (preview && label) {
   672→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
   673→      renderPreview(textarea, preview, label);
   674→    }
   675→  });
   676→});
   677→</script>
