679→<!-- To handle the search bar -->
680→<script>
681→document.addEventListener('DOMContentLoaded', function () {
682→    function setFilterLink(linkId, newFilter) {
683→        const link = document.getElementById(linkId);
684→        if (!link) return;
685→        const pathParts = window.location.pathname.split("/");
686→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
687→        if (pathParts.length >= 3) {
688→            const section = pathParts[1];
689→            const entry = pathParts[2];
690→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
691→            const baseUrl = "/" + section + "/" + entry;
692→            link.href = baseUrl + "/" + newFilter;
693→        }
694→    }
695→    setFilterLink("solvedLink", "solved");
696→    setFilterLink("openLink", "open");
697→    setFilterLink("yesLink", "yes");
698→    setFilterLink("noLink", "no");
