702→<!-- Handle the dual view link -->
703→<script>
704→document.addEventListener("DOMContentLoaded", function () {
705→  const dualLink = document.getElementById("dualViewLink");
706→  if (!dualLink) return;
707→
708→  const isInDualView = window.location.pathname.startsWith("/dual");
709→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
710→  dualLink.addEventListener("click", function (e) {
711→    e.preventDefault();
712→
713→    if (isInDualView) {
714→      try {
715→        const leftIframe = document.getElementById("iframe-left");
716→        const src = leftIframe?.src || "/";
717→        let clean = src.replace(window.location.origin, "");
718→        clean = clean.replace(/[?&]embed=1/, "");
719→
720→        window.location.href = clean;
721→      } catch (err) {
722→        window.location.href = "/";
723→      }
724→    } else {
725→      const currentPath = window.location.pathname + window.location.search;
726→      const rightPanelDefault = "/";
727→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
728→      window.location.href = `/dual${hash}`;
729→    }
730→  });
731→});
732→</script>
