734→<!-- Redirect links and searches when in dual view mode -->
735→<script>
736→document.addEventListener("DOMContentLoaded", function () {
737→  if (!window.location.pathname.startsWith("/dual")) return;
738→
739→  function getTargetPanel() {
740→    const selected = document.querySelector('input[name="targetPanel"]:checked');
741→    return selected ? selected.value : "left";
742→  }
743→
744→  function loadInPanel(panel, path) {
745→    if (!path) return;
746→    const iframe = document.getElementById(`iframe-${panel}`);
747→    if (!iframe) return;
748→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
749→    iframe.src = url;
750→    const inputBox = document.querySelector(`#panel-${panel} input`);
751→    if (inputBox) inputBox.value = path;
752→    console.log(`Loaded "${url}" into panel "${panel}"`);
753→  }
754→  
755→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
756→    link.addEventListener("click", function (e) {
757→      e.preventDefault();
758→      const path = this.getAttribute("href");
759→      loadInPanel(getTargetPanel(), path);
760→    });
761→  });
762→
763→  const idForm = document.getElementById("idsearchForm");
764→  if (idForm) {
765→    idForm.addEventListener("submit", function (e) {
766→      e.preventDefault();
767→      const input = idForm.querySelector(".idsearchTerm").value.trim();
768→      if (!input) return;
769→
770→      let path = null;
771→
772→      if (/^\d+$/.test(input)) {
773→        path = "/go_to/" + encodeURIComponent(input);
774→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
775→        const parts = input.split(/\s+/).filter(Boolean);
776→        path = "/range/" + parts.map(encodeURIComponent).join("/");
777→      } else {
778→        alert("Invalid input format.");
779→        return;
780→      }
781→
782→      loadInPanel(getTargetPanel(), path);
783→    });
784→  }
785→
786→  const searchForm = document.getElementById("searchForm");
787→  if (searchForm) {
788→    searchForm.addEventListener("submit", function (e) {
789→      e.preventDefault();
790→      const input = searchForm.querySelector(".searchTerm").value.trim();
791→      if (!input) return;
792→
793→      const path = "/search/" + encodeURIComponent(input);
794→      loadInPanel(getTargetPanel(), path);
795→    });
796→  }
797→});
798→</script>
