631→<!-- Previewing forum posts -->
632→<script>
633→document.addEventListener("DOMContentLoaded", function () {
634→  function escapeHtml(str) {
635→    return str.replace(/[&<>"']/g, m => ({
636→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
637→    })[m]);
638→  }
639→  function normalizeHref(href){
640→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
641→      href = 'https://' + href;
642→    }
643→    return href;
644→  }
645→
646→  function renderPreview(textarea, preview, label) {
647→    let raw = textarea.value;
648→    const anchors = [];
649→    let idx = 0;
650→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
651→      (match, qHref, labelText) => {
652→        const href = qHref.slice(1, -1);
653→        const token = `__A_TAG_${idx++}__`;
654→        anchors.push({ token, href: normalizeHref(href), label: labelText });
655→        return token;
656→      });
657→    let safe = escapeHtml(raw);
658→    anchors.forEach(a => {
659→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
660→      safe = safe.replace(a.token, html);
661→    });
662→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
663→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
664→    );
665→    safe = safe.replace(/\n/g, "<br>");
666→    if (!safe.trim()) {
667→      preview.classList.add("hidden");
668→      label.classList.add("hidden");
669→      return;
670→    }
671→    preview.innerHTML = safe;
672→    preview.classList.remove("hidden");
673→    label.classList.remove("hidden");
674→    if (window.MathJax && MathJax.Hub) {
675→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
676→    }
677→  }
678→  document.querySelectorAll("form textarea").forEach(textarea => {
679→    const form = textarea.closest("form");
680→    const preview = form && form.querySelector(".post-preview");
681→    const label = form && form.querySelector(".preview-label");
682→    if (preview && label) {
683→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
684→      renderPreview(textarea, preview, label);
685→    }
686→  });
687→});
688→</script>
