638→<!-- Previewing forum posts -->
639→<script>
640→document.addEventListener("DOMContentLoaded", function () {
641→  function escapeHtml(str) {
642→    return str.replace(/[&<>"']/g, m => ({
643→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
644→    })[m]);
645→  }
646→  function normalizeHref(href){
647→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
648→      href = 'https://' + href;
649→    }
650→    return href;
651→  }
652→
653→  function renderPreview(textarea, preview, label) {
654→    let raw = textarea.value;
655→    const anchors = [];
656→    let idx = 0;
657→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
658→      (match, qHref, labelText) => {
659→        const href = qHref.slice(1, -1);
660→        const token = `__A_TAG_${idx++}__`;
661→        anchors.push({ token, href: normalizeHref(href), label: labelText });
662→        return token;
663→      });
664→    let safe = escapeHtml(raw);
665→    anchors.forEach(a => {
666→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
667→      safe = safe.replace(a.token, html);
668→    });
669→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
670→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
671→    );
672→    safe = safe.replace(/\n/g, "<br>");
673→    if (!safe.trim()) {
674→      preview.classList.add("hidden");
675→      label.classList.add("hidden");
676→      return;
677→    }
678→    preview.innerHTML = safe;
679→    preview.classList.remove("hidden");
680→    label.classList.remove("hidden");
681→    if (window.MathJax && MathJax.Hub) {
682→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
683→    }
684→  }
685→  document.querySelectorAll("form textarea").forEach(textarea => {
686→    const form = textarea.closest("form");
687→    const preview = form && form.querySelector(".post-preview");
688→    const label = form && form.querySelector(".preview-label");
689→    if (preview && label) {
690→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
691→      renderPreview(textarea, preview, label);
692→    }
693→  });
694→});
695→</script>
