   747→  }
   748→
   749→  function loadInPanel(panel, path) {
   750→    if (!path) return;
   751→    const iframe = document.getElementById(`iframe-${panel}`);
   752→    if (!iframe) return;
   753→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
