   782→      } else {
   783→        alert("Invalid input format.");
   784→        return;
   785→      }
   786→
   787→      loadInPanel(getTargetPanel(), path);
   788→    });
   789→  }
   790→
   791→  const searchForm = document.getElementById("searchForm");
