   793→    searchForm.addEventListener("submit", function (e) {
   794→      e.preventDefault();
   795→      const input = searchForm.querySelector(".searchTerm").value.trim();
   796→      if (!input) return;
   797→
   798→      const path = "/search/" + encodeURIComponent(input);
   799→      loadInPanel(getTargetPanel(), path);
   800→    });
