623→<!-- Previewing forum posts -->
624→<script>
625→document.addEventListener("DOMContentLoaded", function () {
626→  function escapeHtml(str) {
627→    return str.replace(/[&<>"']/g, m => ({
628→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
629→    })[m]);
630→  }
631→  function normalizeHref(href){
632→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
633→      href = 'https://' + href;
634→    }
635→    return href;
636→  }
637→
638→  function renderPreview(textarea, preview, label) {
639→    let raw = textarea.value;
640→    const anchors = [];
641→    let idx = 0;
642→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
643→      (match, qHref, labelText) => {
644→        const href = qHref.slice(1, -1);
645→        const token = `__A_TAG_${idx++}__`;
646→        anchors.push({ token, href: normalizeHref(href), label: labelText });
647→        return token;
648→      });
649→    let safe = escapeHtml(raw);
650→    anchors.forEach(a => {
651→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
652→      safe = safe.replace(a.token, html);
653→    });
654→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
655→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
656→    );
657→    safe = safe.replace(/\n/g, "<br>");
658→    if (!safe.trim()) {
659→      preview.classList.add("hidden");
660→      label.classList.add("hidden");
661→      return;
662→    }
663→    preview.innerHTML = safe;
664→    preview.classList.remove("hidden");
665→    label.classList.remove("hidden");
666→    if (window.MathJax && MathJax.Hub) {
667→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
668→    }
669→  }
670→  document.querySelectorAll("form textarea").forEach(textarea => {
671→    const form = textarea.closest("form");
672→    const preview = form && form.querySelector(".post-preview");
673→    const label = form && form.querySelector(".preview-label");
674→    if (preview && label) {
675→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
676→      renderPreview(textarea, preview, label);
677→    }
678→  });
679→});
680→</script>
